function createArrayZero(x, y) {
	var array = new Array(x);
	for (var i = 0; i < array.length; i++)
		array[i] = new Array(y);
	for (var i = 0; i < array.length; i++)
		for (var j = 0; j < array[i].length; j++)
			array[i][j] = 0;
	return array;
}
exports.createArrayZero = createArrayZero;

function toIntArray(array) {
	for (var i = 0; i < array.length; i++)
		array[i] = Number(array[i]);
	return array;
}
exports.toIntArray = toIntArray;

function parsePos(pos_str, limit) {
	var pos = pos_str.split(":");
	for (var i = 0; i < pos.length; i++)
		pos[i] = Number(pos[i]);
	if (pos[0] < 0 || pos[0] >= limit[0] || pos[1] < 0 || pos[1] >= limit[1])
		return false;
	return pos;
}
exports.parsePos = parsePos;